mapper.select:
SELECT * FROM STAFF_JOB A


        AND EXISTS (
            SELECT 1 FROM DUAL WHERE A.REGION_ID=#{regionId}
            UNION ALL
            SELECT 1 FROM PUB_REGION A2 WHERE A2.ID=#{regionId} AND A.REGION_ID=A2.SUPER_ID
            UNION ALL
            SELECT 1 FROM PUB_REGION A2 JOIN PUB_REGION A3 ON A3.SUPER_ID=A2.ID WHERE A3.ID=#{regionId} AND A.REGION_ID=A2.SUPER_ID
        )

    AND A.STAFF_ID=#{staffId}
